import {
  GET_FILM,
  UPDATE_FILM,
  GET_FILMS,
  FILM_ERROR,
  CLEAR_FILM
} from '../actions/types';


interface RepositoriesState {
  film: string;
  films: string;
  loading: boolean;
  error: string | null;
};

const initialState = {
  film: null,
  films: [],
  loading: true,
  error: {}
};

interface GetFilm {
  type: typeof GET_FILM;
  film: string[];
  loading: boolean; 
}

interface GetFilms {
  type: typeof GET_FILMS;
  film: string[];
  loading: boolean; 
};

interface UpdateFilm {
  type: typeof UPDATE_FILM;
  film: string[];
  loading: boolean; 
};

interface FilmError {
  type: typeof FILM_ERROR;
  film: string;
  loading: boolean; 
};

interface ClearFilm {
  type: typeof CLEAR_FILM;
  film: string;
  films: string[];
  loading: boolean; 
};

const filmReducer = (
// export default function = (
  state: typeof initialState, 
  action: 
  | GetFilm 
  | GetFilms 
  | UpdateFilm 
  | FilmError 
  | ClearFilm
  ): RepositoriesState => {
  // export default function(state = initialState, action) {
         // const { type, payload } = action 
        switch (action) {
          case 'GET_FILM':
          return {
            ...state,
            film: payload,
            loading: false 
          };
          case 'GET_FILMS':
          return {
            ...state,
            films: payload,
            loading: false 
          };
          case 'UPDATE_FILM':
          return {
            ...state,
            film: payload,
            loading: false 
          }; 
          case 'FILM_ERROR':
          return {
            ...state,
            error: payload,
            loading: false
          }; 
          //this needed?
          case 'CLEAR_FILM':
          return {
            ...state,
            film: null,
            films: [],
            loading: false
          };
          default:
            return state
        }
  }

  export default filmReducer;
