import React, { Fragment } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Spinner from '../spinner/Spinner';
import Ticket from '../ticket/Ticket';
import { userType, ticketType } from '../../utils/componentTypes';
import '../../App.css';
import './Dashboard.css'; 

interface DashTicketsProps {
    user: userType;
    film: number;
}

const DashboardTickets: React.FC<DashTicketsProps> = ({ user, film }) => {

  const { tickets, loading } = user
  return (
     loading && user && tickets === null ? <Spinner /> : 
        <Fragment>
        {user.tickets.length === 0 ? 
          (
         <Fragment >  
              <p className='films-link'>You haven't bought any tickets yet</p>
          <Link  to="/">
            <p className="films-link">Search films</p> 
          </Link>

          </Fragment>
          ) : 
          //await film update for film time
          film ? 
          (   
        <Fragment>

        <div className='your-tickets'>
        {tickets.map(item => (         
             <div 
             className="singleTicket"
             key={item._id}>
             <Ticket
               ticket={item}
               film={film}
             />
             </div> 
        ))}
        </div> 

        </Fragment>
         ) : null
       }    
      </Fragment>
    )

  }  

const mapStateToProps = state => ({
  user: state.auth.user,
  film: state.film.film.filmtime,

});

export default connect(mapStateToProps)(DashboardTickets);
