import React, { Fragment } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Spinner from '../spinner/Spinner';
import Ticket from '../ticket/Ticket';
import { userType, ticketType, filmType } from '../../utils/componentTypes';
import '../../App.css';
import './Dashboard.css'; 

interface DashTicketsProps {
    user: userType;
    film: filmType;
    filmtime: string | number | null;
}

const DashboardTickets: React.FC<DashTicketsProps> = ({ user, film }) => {



  const { tickets, loading } = user
  const { filmtime } = film;

  return (
     tickets && film === null ? <Spinner /> : 
        <Fragment>
        {user.tickets.length === 0 ? 
          (
         <Fragment >  
              <p className='films-link'>You haven't bought any tickets yet</p>
          <Link  to="/">
            <p className="films-link">Search films</p> 
          </Link>

          </Fragment>
          ) : (   
        <Fragment>

        <div className='your-tickets'>
        {tickets.map(item => (         
             <div 
             className="singleTicket"
             key={item._id}>
             <Ticket
               ticket={item}
               filmtime={filmtime}
             />
             </div> 
        ))}
        </div> 

        </Fragment>
         )
       }    
      </Fragment>
    )

  }  

const mapStateToProps = state => ({
  user: state.auth.user,
  //this is coming through as null
  film: state.film.film,

});

export default connect(mapStateToProps)(DashboardTickets);
