import axios from 'axios';
import { ActionType } from './types';
import { ActionFilm } from '../reducers/indexTypes';
// import { setAlert } from './alert';
import {
  GET_FILM,
  UPDATE_FILM,
  GET_FILMS,
  CLEAR_FILM,
  FILM_ERROR,
  // DELETE_FILM
} from './types';

interface formType {

}

// Get all Films
export const loadFilms = () => async (dispatch: Dispatch<ActionFilm>) => {
  try {
    const res = await axios.get('/api/film');

    dispatch({
      type: ActionType.GET_FILMS,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: 'FILM_ERROR',
      //payload: { msg: err.response.statusText, status: err.response.status }
    });
  }
  };

// Get film by ID
export const getFilmById = (film_id: string) => async (dispatch: Dispatch<ActionFilm>) => 
{
  dispatch({ type: ActionType.CLEAR_FILM });
  try {
    const res = await axios.get(`/api/film/${film_id}`)
    // .populate('film', ['title', 'cinema']);

    dispatch({
      type: ActionType.GET_FILM,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: 'FILM_ERROR',
      payload: { msg: err.response.statusText, status: err.response.status }
    });
  }
};

