    if (errors) {
      alert(errors.forEach(error => dispatch(alert(error.msg, 'danger'))));
    }

    dispatch({
      type: 'FILM_ERROR',
      payload: { msg: err.response.statusText, status: err.response.status }
    });
  }
};

