import axios from 'axios';
import { ActionType } from './types';
import { ActionUser } from '../reducers/indexTypes';
import { Dispatch } from 'redux';
import { RouteComponentProps } from 'react-router-dom';
import setAuthToken from '../utils/setAuthToken';
// import {
//   REGISTER_SUCCESS,
//   REGISTER_FAIL,
//   USER_LOADED,
//   UPDATE_USER,
//   LOGIN_SUCCESS,
//   LOGIN_FAIL,
//   LOGOUT,
//   AUTH_ERROR
// } from './types';

interface ChildComponentProps extends RouteComponentProps<any> {
 history: any;
}

// Load User
export const loadUser = () => async (dispatch: Dispatch<ActionUser | any>) => {

  if (localStorage.token) {
    setAuthToken(localStorage.token);
  }

  try {
    const res = await axios.get('/api/auth');

    dispatch({
      type: ActionType.USER_LOADED,
      payload: res.data
    });
  } catch (err) {
    console.log(err)
    dispatch({
      type: ActionType.AUTH_ERROR
    });
  }
};

// interface RegisterTypes {
//   name: string;
//   email: string; 
//   password: string;
// }



// Register User
export const register = ( name: string, email: string, password: string ) => 
async (dispatch: Dispatch<ActionUser>) => {    

  const config = {
    headers: {
      'Content-Type': 'application/json'
    }
  };

  const body = JSON.stringify({ name, email, password });

  try {
    const res = await axios.post('/api/users', body, config);

    dispatch({
      type: ActionType.REGISTER_SUCCESS,
      payload: res.data
    });
    // dispatch(loadUser());
    loadUser();


    } catch (err) {
      console.log('the err is ' + err)
    // const errors = err.response.data.errors;
    if (err instanceof Error) {
    const errors = err.response.data.errors;
      }
    // const errors = 'There was a problem registering';

    if (errors) {
      console.log(err)
      alert(err)
    }

    dispatch({
      type: ActionType.REGISTER_FAIL
    });
  }
};

//Login User
export const login = (email: string, password: string) => async dispatch => {
  const config = {
    headers: {
      'Content-Type': 'application/json'
    }
  };

  const body = JSON.stringify({ email, password });

  try {
    const res = await axios.post('/api/auth', body, config);
    dispatch({
      type: ActionType.LOGIN_SUCCESS,
      payload: res.data
    })
    dispatch(loadUser())
  } 
  catch (err) {

    const errors = err.response.data.errors;
    if (errors) {
      alert(errors)
      dispatch({
      type: ActionType.LOGIN_FAIL
    });
    }
  }

};

// Add Tickets
export const updateUserTickets: React.SFC<ChildComponentProps> = (formData: string, history) => async dispatch => {
  try {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    const res = await axios.post('/api/users/ticket', formData, config);
    dispatch({
      type: ActionType.UPDATE_USER,
      payload: res.data
    });
    //history.push('/film/dashboard');
  } catch (err) {
    const errors = err.response.data.errors;
    if (errors) {
      alert(errors)
      console.log(errors)
    }
  }
};

export const deleteTickets = (id: string) => async dispatch => {
  try {
    const res = await axios.delete(`/api/tickets/${id}`);

    dispatch({
      type: ActionType.UPDATE_USER,
      payload: res.data
    });
  } catch (err) {
    alert(err.response.statusText)
    console.log(err.response.statusText)

  }
};

//Logout
export const logout = () => dispatch => {
  dispatch({ type: ActionType.LOGOUT });
};
