import { ActionType } from '../actions/types';
import { ActionFilms } from './indexTypes';

interface RepositoriesState {
  film: {} | null;
  films: string[];
  loading: boolean;
  error: any; 
  // { 
  //   msg: string,
  //   status: string,
  // } | null
};

const initialState = {
  film: null,
  films: [],
  loading: true,
  error: {}
};

const film = (
// export default function = (
  state: RepositoriesState = initialState,
  action: ActionFilms,
  ): RepositoriesState => {
  // export default function(state = initialState, action) {
         // const { type, payload } = action 
        const { type } = action;
        let payload;  
        if(action.payload) {
          payload = action.payload; 
        }
        switch (action.type) {
          case ActionType.GET_FILM:
          return {
            ...state,
            film: payload,
            loading: false 
          };
          case ActionType.GET_FILMS:
          return {
            ...state,
            films: payload,
            loading: false 
          };
          case ActionType.UPDATE_FILM:
          return {
            ...state,
            film: payload,
            loading: false 
          }; 
          case ActionType.FILM_ERROR:
          return {
            ...state,
            error: payload,
            loading: false
          }; 
          //this needed?
          case ActionType.CLEAR_FILM:
          return {
            ...state,
            film: null,
            films: [],
            loading: false
          };
          default:
            return state
        }
    }

  export default film;
