import React, { Fragment, useEffect } from 'react';
import { withRouter, RouteComponentProps } from 'react-router-dom';
import { connect, useDispatch } from 'react-redux';
import { getFilmById, updateFilm } from '../actions/film';
import CheckoutFilm from '../components/checkout-film/Checkout-Film'

// what is actionCreators in the video??
// use that to dispatch so you bypass the ts validator 


interface ChildComponentProps extends RouteComponentProps<any> {
	match: any
	history: any
}

interface BookingProps {
	// updateFilm: () => void;
	getFilmById: (id) => void;
	// getFilmById: Promise<void>;

	filmBooking: {
		film: {
		_id: number, 
	    title: string,
	    date: number,
	    filmtime: number,
	    cinema: string,
	    image: string,
	    ticketPrice: number,
	    crowdfundTarget: number,
	    totalsoFar: number
	},
	loading: boolean;
	};

}

type JointBookingProps = ChildComponentProps & BookingProps; 

const BookingPage: React.FC<JointBookingProps> = ({
	// updateFilm,
	getFilmById,
	//where is this called from?
	filmBooking: {film, loading},
	match,
	history,
	filmBooking
}) => { 
	useEffect(() => {
		getFilmById(match.params.id)
	}, []);

	return (
		<Fragment>
			<div>
			{film ? <CheckoutFilm/> : <p>please wait</p> }
			</div>
		<div className='checkout-page'>
          * Please use the following test credit card for payments  
          <br />
          4242 4242 4242 4242 - Exp 01/21 - CVV: 123
        </div>
        </Fragment>
		)
	};


const mapStateToProps = state => ({
	filmBooking: state.film
});

export default connect(mapStateToProps, { getFilmById  })(
	withRouter(BookingPage)
);
// export default connect(mapStateToProps)(BookingPage)
