// import {
//   GET_FILM,
//   UPDATE_FILM,
//   GET_FILMS,
//   FILM_ERROR,
//   CLEAR_FILM
// } from '../actions/types';


interface RepositoriesState {
  film: string;
  films: string;
  loading: boolean;
  error: string | null;
};

const initialState = {
  film: null,
  films: [],
  loading: true,
  error: {}
};

interface GetFilm {
  type: 'GET_FILM';
  film: string[];
  loading: boolean; 
}

interface GetFilms {
  type: 'GET_FILMS';
  film: string[];
  loading: boolean; 
};

interface UpdateFilm {
  type: 'UPDATE_FILM';
  film: string[];
  loading: boolean; 
};

interface FilmError {
  type: 'FILM_ERROR';
  film: string;
  loading: boolean; 
};

interface ClearFilm {
  type: 'CLEAR_FILM';
  film: string;
  films: string[];
  loading: boolean; 
};

const filmReducer = (
// export default function = (
  state: RepositoriesState, 
  action: 
  | GetFilm 
  | GetFilms 
  | UpdateFilm 
  | FilmError 
  | ClearFilm
  ): RepositoriesState => {
  // export default function(state = initialState, action) {
         const { type, payload } = action 
        switch (action) {
          case 'GET_FILM':
          return {
            ...state,
            film: payload,
            loading: false 
          };
          case 'GET_FILMS':
          return {
            ...state,
            films: payload,
            loading: false 
          };
          case 'UPDATE_FILM':
          return {
            ...state,
            film: payload,
            loading: false 
          }; 
          case 'FILM_ERROR':
          return {
            ...state,
            error: payload,
            loading: false
          }; 
          //this needed?
          case 'CLEAR_FILM':
          return {
            ...state,
            film: null,
            films: [],
            loading: false
          };
          default:
            return state
        }
  }
