import React, { Fragment } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Spinner from '../spinner/Spinner';
import Ticket from '../ticket/Ticket';
import { userType, filmType, ticketType } from '../../utils/componentTypes';
import '../../App.css';
import './Dashboard.css'; 

interface DashTicketsProps {
    user: userType;
    loading: boolean;
    film: filmType;
    ticket: ticketType;

}


const DashboardTickets: React.FC<DashTicketsProps> = ({ user, loading, film }) => {

  return (
     loading && user === null ? <Spinner /> : 
        <Fragment>
        {user.tickets.length === 0 ? 
          (
         <Fragment >  
              <p className='films-link'>You haven't bought any tickets yet</p>
          <Link  to="/">
            <p className="films-link">Search films</p> 
          </Link>

          </Fragment>
          ) :
          (   
        <Fragment>

        <div className='your-tickets'>
        {user.tickets.map(item => (         
             <div 
             className="singleTicket"
             key={item._id}>
             <Ticket
               ticket={item}
               film={film}
             />
             </div> 
        ))}
        </div> 

        </Fragment>
         ) 
       }    
      </Fragment>
    )

  }  

//export default DashboardTickets;
const mapStateToProps = state => ({
  film: state.film,
  ticket: state.auth.ticket
});

export default connect(mapStateToProps)(Ticket);
