import React, { Fragment, useEffect } from 'react';
import { connect } from 'react-redux';
import Footer from '../components/footer/Footer';
import { loadFilms } from '../actions/film';
import DashboardTickets from '../components/user-dashboard/Dashboard-Tickets';
import DashboardFilms from '../components/user-dashboard/Dashboard-Films';

interface DashboardProps {
  // auth: 
  // loading: boolean,
  auth: {
    // sos user is an object of keys and values, how to define?
    isAuthenticated: boolean,

    token: string,  
        user: {
          email: string,
          name: string,
          tickets: number,
          __v: number
          _id: string,
    },
  loading: boolean;
  };
  user: {
    // sos user is an object of keys and values, how to define?
          email: string,
          name: string,
          tickets: number,
          __v: number
          _id: string,
    },
  // this is an array of objects
    film: {
    films: Array<{
          cinema: string,
          crowdfundTarget: number,
          date: string,
          filmtime: string,
          image: string,
          ticketPrice: number,
          title: string,
          totalsoFar: number,
          user: string,
          __v: number,
          _id: string,
        }>
      };
  loadFilms: () => void;
}


// const Dashboard: React.FC<DashboardProps> = ({ 
  const Dashboard = ({ 
  auth,
  auth: { user, loading }, 
  film: { films },
  loadFilms,
  history
  }) => {

  useEffect(() =>  {
      loadFilms();  
  // }, []);
  }, [loadFilms]); 
  // change back to this when ready

  console.log(auth)
  // console.log(film)
  // console.log('called')


return ( 
        <Fragment>
          <div>
          <h2 className='welcome-message'>Welcome {user && user.name}</h2>
          </div>
          <div className="dashboard-display">
          <div className='tickets-films-block'>
          <h2 className='ticket-film-head'>Your tickets</h2>
          <DashboardTickets
          user={user}
          loading={loading}
           />
          </div>
          <div className='tickets-films-block'>
          <h2 className='ticket-film-head'>Your films</h2>
          <DashboardFilms
          user={user}
          films={films}
          />
          </div>
          </div>
          <Footer />
        </Fragment>
      )
}

const mapStateToProps = state => ({
 auth: state.auth,
 film: state.film,
});

export default connect(mapStateToProps, { loadFilms })(Dashboard);
