import {
  GET_FILM,
  UPDATE_FILM,
  GET_FILMS,
  FILM_ERROR,
  CLEAR_FILM
} from '../actions/types';


interface RepositoriesState {
  film: {} | null;
  films: string[];
  loading: boolean;
  error: any; 
  // { 
  //   msg: string,
  //   status: string,
  // } | null
};

const initialState = {
  film: null,
  films: [],
  loading: true,
  error: {}
};

interface GetFilm {
  type: GET_FILM;
  payload: string[];
}

interface GetFilms {
  type: typeof GET_FILMS;
  payload: string[];
};

// interface UpdateFilm {
//   type: typeof UPDATE_FILM;
// };

interface FilmError {
  type: typeof FILM_ERROR;
  payload: string[];
};

// interface ClearFilm {
//   type: typeof CLEAR_FILM;
// };

const filmReducer = (
// export default function = (
  state: RepositoriesState = initialState,
  action: 
  | GetFilm 
  | GetFilms 
  // | UpdateFilm 
  | FilmError 
  // | ClearFilm
  ): RepositoriesState => {
  // export default function(state = initialState, action) {
         const { type, payload } = action 
        switch (action) {
          case typeof GET_FILM:
          return {
            ...state,
            film: payload,
            loading: false 
          };
          case GET_FILMS:
          return {
            ...state,
            films: payload,
            loading: false 
          };
          case UPDATE_FILM:
          return {
            ...state,
            film: payload,
            loading: false 
          }; 
          case FILM_ERROR:
          return {
            ...state,
            error: payload,
            loading: false
          }; 
          //this needed?
          case CLEAR_FILM:
          return {
            ...state,
            film: null,
            films: [],
            loading: false
          };
          default:
            return state
        }
  }

  export default filmReducer;
