import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { FormContainer } from './stripe-button.styles';
import { stripePayment } from '../../actions/film';
// import { BUTTON_TYPE_CLASSES } from '../button/button.component';

import { PaymentButton, PaymentFormContainer } from './stripe-button.styles';
import axios from 'axios';


const StripeCheckoutButton = () => {

const stripe = useStripe();
const elements = useElements();
let amount = 2;
let currentUser = 'bob';
const [isProcessingPayment, setIsProcessingPayment] = useState(false)

const paymentHandler = async (e) => {
	  	//console.log('called')

	e.preventDefault();
	if(!stripe || !elements) {
		return;
	}

	setIsProcessingPayment(true);

// 	const { error, paymentMethod } =  await stripe.createPaymentMethod({
// 		type: 'card',
// 		card: elements.getElement(CardElement)
// 	})

// 	if(!error) {
// 		console.log(paymentMethod)
// 		const { id } = paymentMethod;

// 	try {
// 		const amount = 100;
// 		//got the info, now....
// 		//how do i post this to the backend?
//     const data = await axios.post('http://localhost:5000/api/film/payment', { id, amount }) 
//     console.log(data)
//   } catch (error) {
//     console.log({ error });
//     };
//   }

// }


		const response = await fetch('http://localhost:5000/api/film/payment', {
			// const response = await axios.post('./payment-intents', {
			method: 'post',
			headers: {
				'Content-Type': 'application/json'
			},
		body: JSON.stringify({ amount: amount * 2 })
     }).catch(console.error)
		.then((res) => res.json())

    	// console.log('called2')
    // 	//console.log(res.json())
    //   return res.json();
    // });
    // console.log('respo ' + response)
    const {
    	paymentIntent: {client_secret},
    } = response;

    console.log(client_secret)
    //const clientSecret = response.paymentIntent.client_secret;
    console.log('clled')
    const paymentResult = await stripe.confirmCardPayment(client_secret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: currentUser ? 'Andrew Higton' : 'Andrew Higton',
        },
      },
    });

    setIsProcessingPayment(false);

    if (paymentResult.error) {
      alert(paymentResult.error.message);
    } else {
      if (paymentResult.paymentIntent.status === 'succeeded') {
        alert('Payment Successful!');
      }
   }
}

	return (

			<div> 
				<PaymentFormContainer>
      		<FormContainer onSubmit={paymentHandler}>
	        	<h2>Credit Card Payment:</h2>
	        	<CardElement />
	        <button>Pay Now</button>
	      </FormContainer>
    		</PaymentFormContainer>
			</div>
		 )
}

export default StripeCheckoutButton;


