import { ActionType } from '../actions/types';

interface GetFilm {
  type: ActionType.GET_FILM;
  payload: string[];
}

interface GetFilms {
  type: ActionType.GET_FILMS;
  payload: string[];
};

interface UpdateFilm {
  type: ActionType.UPDATE_FILM;
  payload: null;
};

interface FilmError {
  type: ActionType.FILM_ERROR;
  payload: string[];
};

interface ClearFilm {
  type: ActionType.CLEAR_FILM;
  payload: null;
};

export type ActionFilms =
  | GetFilm
  | GetFilms
  | UpdateFilm
  | ClearFilm
  | FilmError;

interface loadUser {
  type: ActionType.USER_LOADED;
  // type: ActionType.AUTH_ERROR;
  payload: string[];
}

//how to add two types, either or? 
interface register {
  type: ActionType.REGISTER_SUCCESS;
  // type: ActionType.REGISTER_FAIL;
  payload: string[];
}

interface login {
  type: ActionType.LOGIN_SUCCESS;
  payload: string[];
}

interface loginFail {
  type: ActionType.LOGIN_FAIL;
  payload: null;
}

interface updateUserTickets {
  type: ActionType.UPDATE_USER;
  payload: string[];
}

interface deleteTickets {
  type: ActionType.UPDATE_USER;
  payload: string[];
}

interface logout {
  type: ActionType.LOGOUT;
  payload: null;
}

// interface registerFail {
//   type: ActionType.LOGOUT;
//   payload: null;
// }

type registerFail = {
  type: ActionType.REGISTER_FAIL;
  payload: null;
}

type authError = {
  type: ActionType.AUTH_ERROR;
  payload: null;
}


export type ActionUser =
  | logout
  | deleteTickets
  | updateUserTickets
  | login
  | loginFail
  | register
  | loadUser
  | registerFail
  | authError
  ;
