	match: any
	history: any
}

interface BookingProps {
	updateFilm: () => void;
	// getFilmById: (id: number) => ();
	filmBooking: {
		film: {
		_id: number, 
	    title: string,
	    date: number,
	    filmtime: number,
	    cinema: string,
	    image: string,
	    ticketPrice: number,
	    crowdfundTarget: number,
	    totalsoFar: number
	},
	loading: boolean;
	};

}

type JointBookingProps = ChildComponentProps & BookingProps; 

const BookingPage: React.FC<JointBookingProps> = ({
	updateFilm,
